import { Box, Center, Container, HoverCard } from "@mantine/core";
import React from "react";
import {
  IconAlignLeft,
  IconSearch,
  IconPhotoMinus,
  IconCircleXFilled,
} from "@tabler/icons-react";
const AppBar = () => {
    const [isSelected, setIsSelected] = useState(false);

  const handleContainerClick = () => {
    setIsSelected(!isSelected);
  };

  const containerStyles = {
    my: 'xs',
    bg: 'var(--mantine-color-blue-light)',
    borderRadius: '5px',
    p: 4,
    transition: 'border-color 0.3s ease-in-out',
    border: isSelected ? '2px solid gray' : '2px solid transparent',
  };


  return (
    <div>
      <Container
        fluid
        bg="var(--mantine-color-blue-light)"
        style={{
          borderRadius: "5px",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <div style={{ display: "inline-block", justifyContent: "flex-center" }}>
          App Bar
        </div>
        <div style={{ display: "flex", justifyContent: "flex-end" }}>
          <IconCircleXFilled />
        </div>
      </Container>
      <Container
        my="xs"
        bg="var(--mantine-color-blue-light)"
        className="rounded-lg p-4 transition duration-300 ease-in-out hover:border-gray-300"
        style={{ borderRadius: "5px" }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-end",
          }}
        >
          <div className="mr-4" style={{ alignItems: "flex-start" }}>
            <IconAlignLeft />
          </div>
          <div style={{ display: "inline-block" }}>
            <IconSearch style={{ display: "inline-block" }} />
            <IconPhotoMinus style={{ display: "inline-block" }} />
          </div>
        </div>
      </Container>
      <Container
        my="xs"
        bg="var(--mantine-color-blue-light)"
        className="rounded-lg p-4 transition duration-300 ease-in-out hover:border-gray-300"
        style={{ borderRadius: "5px" }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-end",
          }}
        >
          <div className="mr-4" style={{ alignItems: "flex-start" }}>
            <IconAlignLeft />
          </div>
          <div style={{ display: "inline-block" }}>
            <IconSearch style={{ display: "inline-block" }} />
            <IconPhotoMinus style={{ display: "inline-block" }} />
          </div>
        </div>
      </Container>
      <Container
        my="xs"
        bg="var(--mantine-color-blue-light)"
        className="rounded-lg p-4 transition duration-300 ease-in-out hover:border-gray-300"
        style={{ borderRadius: "5px" }}
      >
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-end",
          }}
        >
          <div className="mr-4" style={{ alignItems: "flex-start" }}>
            <IconAlignLeft />
          </div>
          <div style={{ display: "inline-block" }}>
            <IconSearch style={{ display: "inline-block" }} />
            <IconPhotoMinus style={{ display: "inline-block" }} />
          </div>
        </div>
      </Container>
    </div>
  );
};

export default AppBar;
