import React from "react";
import { Flex, Grid, Box, ThemeIcon } from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconSearch,
  IconMessageCircle,
  IconShoppingBag,
  IconBookmark,
} from "@tabler/icons-react";

const icons = [
  { name: "IconHome2", component: <IconHome2 /> },
  { name: "IconMessageCircle", component: <IconMessageCircle /> },
  { name: "IconSearch", component: <IconSearch /> },
  { name: "IconShoppingBag", component: <IconShoppingBag /> },
  { name: "IconUser", component: <IconUser /> },
];

const navbarSections = [
  {
    icons: icons,
    gradientFrom: "green",
    gradientTo: "indigo",
    deg: 360,
    bgColor: "white",
  },
  {
    icons: icons,
    gradientFrom: "violet",
    gradientTo: "red",
    deg: 155,
    bgColor: "white",
  },
  {
    icons: icons,
    gradientFrom: "red",
    gradientTo: "green",
    deg: 360,
    bgColor: "dark.9",
  },
];

const Navbar = ({ Item, SetItem, Item2, setItem2, Item3, setItem3, navBar }) => {
  const handleStackClick = (index) => {
    switch (index) {
      case 0:
        SetItem(!Item);
        break;
      case 1:
        setItem2(!Item2);
        break;
      case 2:
        setItem3(!Item3);
        break;
      default:
        break;
    }

    const section = navbarSections[index];
    navBar(
      <Flex mih={40} mt="xs" bg={section.bgColor} justify="space-around" align="center" direction="row" wrap="wrap" style={{ border: "1px solid #1b1c1b" }}>
        {section.icons.map((icon, index) => (
          <ThemeIcon
            variant="gradient"
            size="md"
            aria-label="Gradient action icon"
            gradient={{
              from: section.gradientFrom,
              to: section.gradientTo,
              deg: section.deg,
            }}
            key={icon.name}
          >
            {icon.component}
          </ThemeIcon>
        ))}
      </Flex>
    );
  };

  return (
    <Grid>
      <Grid.Col span={6}>
        {navbarSections.map((section, index) => (
          <Box key={index} onClick={() => handleStackClick(index)}>
            {navBar(
              <Flex mih={40} mt="xs" bg={section.bgColor} justify="space-around" align="center" direction="row" wrap="wrap" style={{ border: "1px solid #1b1c1b" }}>
                {section.icons.map((icon, index) => (
                  <ThemeIcon
                    variant="gradient"
                    size="md"
                    aria-label="Gradient action icon"
                    gradient={{
                      from: section.gradientFrom,
                      to: section.gradientTo,
                      deg: section.deg,
                    }}
                    key={icon.name}
                  >
                    {icon.component}
                  </ThemeIcon>
                ))}
              </Flex>
            )}
          </Box>
        ))}
      </Grid.Col>
    </Grid>
  );
};

export default Navbar;
