import React, { useState } from "react";
import {
  Text,
  Flex,
  Accordion,
  Checkbox,
  ColorPicker,
  Input,
  Image,
} from "@mantine/core";

const CustomizeCategoryList3 = () => {
  const [categoryHeaders, setCategoryHeaders] = useState({
    allProduct_level: "All Product",
    electronic_level: "Electronics",
    clothing_level: "Clothing",
  });

  const updateProperty = (property, value) => {
    setCategoryHeaders({ ...categoryHeaders, [property]: value });
  };

  const handleChange = (property, text) => {
    updateProperty(property, text);
  };

  const accordionItems = [
    {
      value: "all-product",
      label: "All Product",
      property: "allProduct_level",
    },
    {
      value: "electronics2",
      label: "Electronics",
      property: "electronic_level",
    },
    {
      value: "clothing2",
      label: "Clothing",
      property: "clothing_level",
    },
  ];

  const accordionComponents = accordionItems.map((item, index) => (
    <Accordion.Item key={index} value={item.value}>
      <Accordion.Control bg="gray.2" direction="row">
        <Flex justify="flex-start" align="center" direction="row" gap="md">
          <Checkbox defaultChecked />
          <Text>{item.label}</Text>
        </Flex>
      </Accordion.Control>
      <Accordion.Panel>
        <Flex direction="column">
          <Text mt="xs">Background Color</Text>
          <ColorPicker mt="xs" w="auto" size="lg" format="rgba" />
        </Flex>
        <Flex direction="column">
          <Text mt="xs">Level</Text>
          <Input
            placeholder="Input component"
            value={categoryHeaders[item.property]}
            onChange={(event) => {
              handleChange(item.property, event.target.value);
            }}
          />
        </Flex>
      </Accordion.Panel>
    </Accordion.Item>
  ));

  return (
    <Accordion m="xs" variant="contained">
      {accordionComponents}

      {/* Add More */}
      <Accordion.Item mt="xs" value="addmore">
        <Accordion.Control bg="gray.2" direction="row">
          <Flex justify="flex-start" align="center" direction="row" gap="md">
            <Text c={`indigo.6`}>+ Add More</Text>
          </Flex>
        </Accordion.Control>
        <Accordion.Panel>
          <Flex direction="column">
            <Text>Background Color</Text>
            <ColorPicker w="auto" size="lg" format="rgba" />
          </Flex>
          <Flex direction="column">
            <Text>Level</Text>
            <Input placeholder="Input component" />
          </Flex>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default CustomizeCategoryList3;
