import { Stack, Text, ColorPicker } from "@mantine/core";
import React from "react";

const Layout3 = ({ onColorChange }) => {
  return (
    <Stack bg="gray.3">
      <Stack>
        <Text style={{ marginLeft: "1rem" }}>Background Color</Text>
        <ColorPicker
          ml="xs"
          mr="xs"
          mb="xs"
          w="auto"
          size="lg"
          format="rgba"
          onChange={(color) => {
            onColorChange(color);
          }}
        />
      </Stack>
    </Stack>
  );
};

export default Layout3;
