import React, { useState } from "react";
import { Box, Text, Accordion } from "@mantine/core";
import { IconX } from "@tabler/icons-react";
import HomeSlider from "./HomeSlider";
import CategoryList from "./CategoryList";
import BrandList from "./BrandList";
import BrandWithProduct from "./BrandWithProduct";
import LatestProduct from "./LatestProduct";
import MostPopulerProduct from "./MostPopulerProduct";
import CategoryWithProduct from "./CategoryWithProduct";

const Layout = ({
  setCategoryListBoxVisible,
  categoryListBoxVisible,
  setCategoryListBoxVisible2,
  categoryListBoxVisible2,
  setCategoryListBoxVisible3,
  categoryListBoxVisible3,
  setCategoryListBoxVisible4,
  categoryListBoxVisible4,
  setCategoryWithProductVisible,
  categoryWithProductVisible,
  setCategoryWithProductVisible2,
  categoryWithProductVisible2,
  categoryWithProductHeaderValue,
  categoryWithProductHeaderValue2,
}) => {
  const [layouttVisible, setLayoutVisible] = useState(true);

  const components = [
    <HomeSlider />,
    <CategoryList
      setBoxVisible={setCategoryListBoxVisible}
      setBoxVisible2={setCategoryListBoxVisible2}
      boxVisible={categoryListBoxVisible}
      boxVisible2={categoryListBoxVisible2}
      setBoxVisible3={setCategoryListBoxVisible3}
      boxVisible3={categoryListBoxVisible3}
      setBoxVisible4={setCategoryListBoxVisible4}
      boxVisible4={categoryListBoxVisible4}
      productCustomize={setCategoryWithProductVisible}
    />,
    <CategoryWithProduct
      setBoxVisible={setCategoryWithProductVisible}
      boxVisible={categoryWithProductVisible}
      setBoxVisible2={setCategoryWithProductVisible2}
      boxVisible2={categoryWithProductVisible2}
      title={categoryWithProductHeaderValue}
      title2={categoryWithProductHeaderValue2}
    />,
    <BrandList
      setBoxVisible={setCategoryListBoxVisible}
      setBoxVisible2={setCategoryListBoxVisible2}
      boxVisible={categoryListBoxVisible}
      boxVisible2={categoryListBoxVisible2}
      setBoxVisible3={setCategoryListBoxVisible3}
      boxVisible3={categoryListBoxVisible3}
      setBoxVisible4={setCategoryListBoxVisible4}
      boxVisible4={categoryListBoxVisible4}
    />,
    <BrandWithProduct
      setBoxVisible={setCategoryWithProductVisible}
      boxVisible={categoryWithProductVisible}
      setBoxVisible2={setCategoryWithProductVisible2}
      boxVisible2={categoryWithProductVisible2}
      title={categoryWithProductHeaderValue}
      title2={categoryWithProductHeaderValue2}
    />,
    <LatestProduct
      setBoxVisible={setCategoryWithProductVisible}
      boxVisible={categoryWithProductVisible}
      setBoxVisible2={setCategoryWithProductVisible2}
      boxVisible2={categoryWithProductVisible2}
      title={categoryWithProductHeaderValue}
      title2={categoryWithProductHeaderValue2}
    />,
    <MostPopulerProduct
      setBoxVisible={setCategoryWithProductVisible}
      boxVisible={categoryWithProductVisible}
      setBoxVisible2={setCategoryWithProductVisible2}
      boxVisible2={categoryWithProductVisible2}
      title={categoryWithProductHeaderValue}
      title2={categoryWithProductHeaderValue2}
    />,
  ];

  const handleLayoutClick = () => {
    setLayoutVisible(false);
  };

  return (
    <>
      {layouttVisible && (
        <Box m="xs" bg="gray.2" style={{ borderRadius: "10px" }}>
          <Box
            bg="indigo.5"
            ml="xs"
            mr="xs"
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "space-between",
              backgroundColor: "indigo.5",
              padding: "10px",
              borderRadius: "10px",
            }}
          >
            <Text
              fw={900}
              c={`#FFFFFF`}
              style={{
                flex: "1",
                textAlign: "center",
              }}
            >
              Layouts
            </Text>
            <IconX
              size={28}
              onClick={handleLayoutClick}
              style={{
                borderRadius: "50%",
                background: "red",
                color: "white",
                alignSelf: "flex-end",
                cursor: "pointer",
              }}
            />
          </Box>

          <Accordion m="xs" pb={10} variant="contained">
            {components.map((component, index) => (
              <Box key={index}>{component}</Box>
            ))}
          </Accordion>
        </Box>
      )}
    </>
  );
};

export default Layout;
