import React, { useState } from "react";
import {
  Box,
  Center,
  Stack,
  Flex,
  ActionIcon,
  Autocomplete,
  rem,
  Avatar,
} from "@mantine/core";
import {
  IconHome2,
  IconUser,
  IconMessageCircle,
  IconBookmark,
  IconAlignLeft,
  IconSearch,
  IconCircleXFilled,
  IconBell,
} from "@tabler/icons-react";

const ResponsivePhone = () => {
  const [selectedBlackIcon, setSelectedBlackIcon] = useState(null);

  const handleBlackIconClick = (index, component) => {
    console.log("Clicked icon :", component);
    setSelectedBlackIcon(index);
  };

  const blackicons = [
    { name: "IconHome2", component: <IconHome2 /> },
    { name: "IconMessageCircle", component: <IconMessageCircle /> },
    { name: "IconBookmark", component: <IconBookmark /> },
    { name: "IconUser", component: <IconUser /> },
  ];

  return (
    <Stack>
      <Center>
        <Box
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            border: "2px solid #1b1c1b",
            borderRadius: "15px",
            width: "200px", // Fixed width
            height: "400px", // Fixed height
            backgroundColor: "#ffffff",
            boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
          }}
        >
          {/* Top Box */}
            {/* Add content for the top section */}
            <Box
              style={{
                width: "200px", // Fixed width for the top box
                height: "50px", // Fixed height for the top box
                backgroundColor: "#ccc", // Background color for the top box
                border: "2px solid #000", // Border for the top box
                borderRadius: "0 0 0 15px", // Border radius for the top box
              }}
            >
              {/* Content of the top box */}
            </Box>

          {/* Bottom Box */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              flex: 1, // Make the bottom section grow to take available space
            }}
          >
            {/* Add content for the bottom section */}
            <p>This is the bottom section</p>
          </div>
        </Box>
      </Center>
    </Stack>
  );
};

export default ResponsivePhone;
