import React, { useState } from "react";
import { IconPlus, IconMinus } from "@tabler/icons-react";
import { Accordion } from "@mantine/core";

const Order = () => {
  const groceries = [
    {
      value: "Apples",
      description:
        "Crisp and refreshing fruit. Apples are known for their versatility and nutritional benefits. They come in a variety of flavors and are great for snacking, baking, or adding to salads.",
    },
  ];

  const [isOpen, setIsOpen] = useState(false);

  const toggleChevronIcon = () => {
    setIsOpen((prevIsOpen) => !prevIsOpen);
  };

  const chevronIcon = isOpen ? <IconMinus /> : <IconPlus />;

  const items = groceries.map((item) => (
    <Accordion.Item key={item.value} value={item.value}>
      <Accordion.Control onClick={toggleChevronIcon}>
        {item.value}
      </Accordion.Control>
      <Accordion.Panel>{item.description}</Accordion.Panel>
    </Accordion.Item>
  ));

  return (
    <Accordion chevronPosition="left" chevron={chevronIcon}>
      <Accordion.Item value="apple">
        <Accordion.Control onClick={toggleChevronIcon}>Apple</Accordion.Control>
        <Accordion.Panel>
          <Accordion.Item value="appble">
            <Accordion.Control onClick={toggleChevronIcon}>
              Apple
            </Accordion.Control>
            <Accordion.Panel>Test</Accordion.Panel>
          </Accordion.Item>
        </Accordion.Panel>
      </Accordion.Item>
    </Accordion>
  );
};

export default Order;
