import React, { useState } from "react";
import {
  Box,
  Text,
  Flex,
  Accordion,
  Center,
  Avatar,
  Badge,
  Checkbox,
} from "@mantine/core";

const CategoryList = ({
  setBoxVisible,
  boxVisible,
  setBoxVisible2,
  boxVisible2,
  setBoxVisible3,
  boxVisible3,
  setBoxVisible4,
  boxVisible4,
  setProductCustomize,
  setProductCustomize2,
}) => {
  const [iconCheckVisible, setIconCheckVisible] = useState(false);
  const [iconCheckVisible2, setIconCheckVisible2] = useState(false);
  const [iconCheckVisible3, setIconCheckVisible3] = useState(false);
  const [iconCheckVisible4, setIconCheckVisible4] = useState(false);

  const handleCategoryWithProductClick = () => {
    setBoxVisible(!boxVisible);
    setBoxVisible2(false);
    setBoxVisible3(false);
    setBoxVisible4(false);
    setProductCustomize(false);
    setProductCustomize2(false);
    setIconCheckVisible(!iconCheckVisible);
    setIconCheckVisible2(false);
    setIconCheckVisible3(false);
    setIconCheckVisible4(false);
  };

  const handleCategoryWithProductClick2 = () => {
    setBoxVisible2(!boxVisible2);
    setBoxVisible(false);
    setBoxVisible3(false);
    setBoxVisible4(false);
    setProductCustomize(false);
    setProductCustomize2(false);
    setIconCheckVisible2(!iconCheckVisible2);
    setIconCheckVisible(false);
    setIconCheckVisible3(false);
    setIconCheckVisible4(false);
  };

  const handleCategoryWithProductClick3 = () => {
    setBoxVisible3(!boxVisible3);
    setBoxVisible(false);
    setBoxVisible2(false);
    setBoxVisible4(false);
    setProductCustomize(false);
    setProductCustomize2(false);
    setIconCheckVisible3(!iconCheckVisible3);
    setIconCheckVisible(false);
    setIconCheckVisible2(false);
    setIconCheckVisible4(false);
  };

  const handleCategoryWithProductClick4 = () => {
    setBoxVisible4(!boxVisible4);
    setBoxVisible(false);
    setBoxVisible2(false);
    setBoxVisible3(false);
    setProductCustomize(false);
    setProductCustomize2(false);
    setIconCheckVisible4(!iconCheckVisible4);
    setIconCheckVisible3(false);
    setIconCheckVisible2(false);
    setIconCheckVisible(false);
  };

  return (
    <Accordion.Item value="category-list">
      <Accordion.Control bg="indigo.2">
        <Center>
          <Text fw={700}>Category List</Text>
        </Center>
      </Accordion.Control>
      <Accordion.Panel bg="gray.2">
        <Box mb="xs">
          <Box
            bg="#FFFFFF"
            style={{ cursor: "pointer" }}
            onClick={handleCategoryWithProductClick}
          >
            <Flex
              h="auto"
              justify="center"
              align="center"
              direction="column"
              gap="sm"
            >
              <Flex justify="center" align="center" direction="column">
                <Avatar
                  variant="filled"
                  radius="sm"
                  size="lg"
                  color="yellow"
                  src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                />
                <Text size="xs">Electronics</Text>
              </Flex>
              {iconCheckVisible && (
                <Flex>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>

          <Box
            bg="dark"
            style={{ cursor: "pointer" }}
            onClick={handleCategoryWithProductClick2}
          >
            <Flex
              h="auto"
              justify="center"
              align="center"
              direction="column"
              gap="sm"
            >
              <Flex justify="center" align="center" direction="column">
                <Avatar
                  variant="filled"
                  radius="sm"
                  size="lg"
                  color="yellow"
                  src="https://blogassets.leverageedu.com/blog/wp-content/uploads/2020/01/02232228/BSc-Electronics.png"
                />
                <Text size="xs" color="gray.0">
                  Electronics
                </Text>
              </Flex>
              {iconCheckVisible2 && (
                <Flex>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>

          <Box
            bg="dark"
            mt="xs"
            style={{ cursor: "pointer" }}
            onClick={handleCategoryWithProductClick3}
          >
            <Flex h="auto" justify="center" align="center" direction="row" gap="sm">
              <Flex gap="sm" justify="center" align="center" direction="row">
                <Badge size="xs" color="blue">
                  All Product
                </Badge>
                <Badge size="xs" color="dark.4">
                  Electronics
                </Badge>
                <Badge size="xs" color="dark.4">
                  Clothing
                </Badge>
              </Flex>
              {iconCheckVisible3 && (
                <Flex>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>

          <Box
            bg="#FFFFFF"
            mt="xs"
            style={{ cursor: "pointer" }}
            onClick={handleCategoryWithProductClick4}
          >
            <Flex h="auto" justify="center" align="center" direction="row" gap="sm">
              <Flex gap="sm" justify="center" align="center" direction="row">
                <Badge size="xs" color="blue">
                  Technology
                </Badge>
                <Badge size="xs" color="dark.4">
                  Fashion
                </Badge>
                <Badge size="xs" color="dark.4">
                  Sports
                </Badge>
                <Badge size="xs" color="dark.4">
                  Home
                </Badge>
              </Flex>
              {iconCheckVisible4 && (
                <Flex>
                  <Checkbox defaultChecked />
                </Flex>
              )}
            </Flex>
          </Box>
        </Box>
      </Accordion.Panel>
    </Accordion.Item>
  );
};

export default CategoryList;
